/* -------------- tab_menu -------------- */
const tabLi = $('.tab_menu .menu_label li')
const detailInfo = $('.tab_menu .menu_label li.detailInfo')
const notice = $('.tab_menu .menu_label li.notice')
const review = $('.tab_menu .menu_label li.review')
const question = $('.tab_menu .menu_label li.question')

let index = 0

tabLi.click(function () {
  tabLi.removeClass('on')
  $(this).addClass('on')
});


document.addEventListener('DOMContentLoaded', () => {

  const moreBtn = document.querySelector('.more_btn')
  const detailBox = document.querySelector('.detail')
  let isOpen = false 
  //변수에 is가 붙으면 false나 true 값을 가지는 변수라는 것
  //status 움직이는 동적 컨텐츠 만들때 많이 사용

  moreBtn.addEventListener('click', function() {

    if(isOpen === false) {
      detailBox.style.display = 'block'
      moreBtn.innerHTML = '상세설명닫기 <i class="fas fa-chevron-up"></i>'
      isOpen = true
    } else {
      detailBox.style.display = 'none'
      btn.innerHTML = '상세설명보기 <i class="fas fa-chevron-down"></i>'
      isOpen = false
    }
  }) //moreBtn_click_event
})


/* -------------- top_btn -------------- */
const topBtn = document.querySelector('.top_btn')


window.addEventListener('scroll', () => {
  if (window.scrollY > 300) {
    gsap.to(topBtn, 0.3, {
      opacity: 1
    })
  } else {
    gsap.to(topBtn, 0.3, {
      opacity: 0
    })
  }
}); //topBtn_event


topBtn.addEventListener('click', () => {
  gsap.to(window, 0.5, {
    scrollTo: 0
  })
});

window.addEventListener('scroll', () => {
  const historyBtn = document.querySelector('.history_btn')

  if (window.scrollY > 300) {
    gsap.to(historyBtn, 0.3, {
      opacity: 1
    })
  } else {
    gsap.to(historyBtn, 0.3, {
      opacity: 0
    })
  }
}); //historyBtn_event


/* -------------- shopping_history -------------- */
document.addEventListener('DOMContentLoaded', () => {
  const historyPopup = document.querySelector('.history_popup')
  const historyBtn = document.querySelector('.history_btn')
  const hideBtn = document.querySelector('.history_hide')
  const shoppingHistory = document.querySelector('.shopping_history')
  const historyList = document.querySelectorAll('.shopping_history .history_list')
  const deleteBtn = document.querySelectorAll('.list_txt .list_delete')
  const deleteAll = document.getElementById('.deleteAll')

  historyBtn.addEventListener('click', showHistory)
  hideBtn.addEventListener('click', hideHistory)
  historyList.addEventListener('mouseover', showBtn)
  deleteBtn.addEventListener('click', deleteList)
  deleteAll.addEventListener('click', deleteHistory)

  function showHistory() {
    historyPopup.style.right = '0';
  }

  function hideHistory() {
    historyPopup.style.display = 'none';
  }

  function showBtn() {
    historyList.style.border = '1px solid #eee';
    deleteBtn.style.color = '#191919';

    historyList.addEventListener('mouseout', () => {
      historyList.style.border = 'none';
      deleteBtn.style.display = 'none';
    });
  }

  function deleteList() {
    historyList.style.display = 'none';
  }

  function deleteHistory() {
    shoppingHistory.style.display = 'none';
  }
});
