* {
  margin: 0;
  padding: 0;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #252117; //#332e22
}

body,
html {
  width: 100%;
  height: 100vh;
  background-color: #f8f8f8;
}

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@100;300;400;500;700;900&family=Poppins:wght@300;400;500;600;700&display=swap');

.kor {
  font-family: 'Noto Sans KR', sans-serif;
}

.eng {
  font-family: 'Poppins', sans-serif;
}

$color : #FD8234;


/* ----------------------- header ----------------------- */
.header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 105px;
  background-color: #f8f8f8;
  box-shadow: rgba(0, 0, 0, 0.09) 0px 3px 12px;
  //box-shadow: rgba(0, 0, 0, 0.16) 0px 3px 6px, rgba(0, 0, 0, 0.23) 0px 3px 6px;
  //box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
  z-index: 99999999;

  .logo {
    width: 63px;
    height: auto;
    padding-top: 21px;
    box-sizing: border-box;
    margin: 0px 0px 0px 60px;
    cursor: pointer;

    a {
      width: 55px;
      height: auto;
      display: block;

      img {
        width: 100%;
      }
    }
  }

  .nav {
    .lnb {
      ul.lnb_main {
        width: 650px;
        height: 100px;
        display: flex;
        justify-content: space-between;
        align-items: flex-start;
        position: absolute;
        top: 57px;
        left: 200px;

        li {
          height: 100px;

          a {
            font-size: 15px;
            font-weight: bold;
            cursor: pointer;
          }

          &:hover a {
            color: $color;
          }
        }
      }

      ul.lnb_detail {
        position: relative;
        width: 100vw;
        z-index: 9;

        .shop_detail_bg {
          width: 520px;
          height: calc(100vh - 105px);
          position: absolute;
          top: 14px;
          left: -869px;
          transition: all 0.3s;

          .shop_detail {
            width: 520px;
            height: calc(100vh - 105px);
            background-color: #F8F6F4;

            & li:hover {
              background-color: #EFE8DA;
            }

            & li:hover a {
              color: $color;
            }

            li {
              width: 100%;
              height: 72px;
              border-bottom: 1px solid #eee;
              padding: 12px 0 0 70px;
              box-sizing: border-box;
              line-height: 50px;
            }

            li a {
              display: block;
              width: 100%;
              height: 68px;
              font-size: 13px;
              font-weight: bold;
              cursor: pointer;
            }

            & li:nth-child(1) {
              width: 50%;
              height: 72px;
              display: inline-block;
            }

            & li:nth-child(2) {
              width: 50%;
              height: 72px;
              float: left;
            }
          }
        }

        .story_detail_bg {
          width: 100vw;
          height: 50px;
          position: absolute;
          top: 16px;
          left: 0;
          display: none;

          .story_detail {
            width: 100vw;
            height: 50px;
            background-color: #EFE8DA;
            display: flex;
            transition: all 0.5s;


            & li:hover a {
              color: $color;
            }

            li {
              padding: 12px 10px 0;
              cursor: pointer;
            }

            li a {
              font-size: 13px;
              font-weight: bold;

            }

            li:nth-child(1) {
              margin: 0 45px 0 305px;
            }
          }
        }

        .support_detail_bg {
          width: 100vw;
          height: 50px;
          position: absolute;
          top: 16px;
          left: 0;
          transition: all 0.5s;
          display: none;

          .support_detail {
            width: 100vw;
            height: 50px;
            background-color: #EFE8DA;
            display: flex;
            transition: all 0.5s;

            & li:hover a {
              color: $color;
            }

            li {
              padding: 12px 10px 0;
              margin-right: 45px;
            }

            li a {
              font-size: 13px;
              font-weight: bold;
            }

            li:nth-child(1) {
              margin-left: 645px;
            }
          }
        }
      }
    }
  }


  .gnb {
    ul {
      width: 400px;
      height: 50px;
      display: flex;
      justify-content: space-between;
      align-items: flex-start;
      position: absolute;
      top: 57px;
      right: 55px;

      li {
        font-size: 15px;
        font-weight: bold;
        cursor: pointer;

        &.search {
          width: 165px;
          height: 25px;
          background-color: $color;
          border-radius: 20px;

          input {
            width: 165px;
            height: 25px;
            padding: 12px 15px;
            box-sizing: border-box;
            color: #f8f8f8;
            background-color: $color;
            border-radius: 20px;
            border: none;
            position: relative;
          }

          i {
            font-size: 13px;
            color: #f8f8f8;
            position: absolute;
            top: 6px;
            right: 246px;
          }
        }
      }

      li:hover a {
        color: $color;
      }

    }
  }
}


/* ----------------------- main ----------------------- */
.main {
  width: 100%;
  height: auto;

  .swiper_main {
    width: auto;
    height: calc(100vh - 15px);

    &:hover .swiperBtn {
      visibility: visible;
      transition: all 0.3s;
    }

    .swiper-wrapper {
      width: auto;
      height: calc(100vh - 15px);

      .swiper-slide {
        width: auto;
        height: calc(100vh - 15px);
        display: flex;
        justify-content: center;
        align-items: center;

        img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }
      }
    }

    .swiper-pagination {
      .swiper-pagination-bullet {
        width: 9px;
        height: 9px;
        background-color: white;
        margin-bottom: 20px;
      }

      .swiper-pagination-bullet-active {
        background-color: $color;
        width: 20px;
        height: 10px;
        border-radius: 10px;
        transition: all 0.3s;
      }
    }

    .swiperBtn {
      margin-top: 20px;
      visibility: hidden;
    }

    .swiperBtn:after {
      color: $color;
      padding: 10px;
      font-size: 30px;
      font-weight: bold;
    }
  }


  /* ----------------------- new_arrivals ----------------------- */
  .new_arrivals {
    width: 100vw;
    height: auto;
    position: relative;

    h1 {
      text-align: center;
      padding: 10px 0;
      margin: 70px 0 50px;
    }

    .swiper_new {
      width: 1180px;
      height: auto;
      margin: 0 auto;

      .swiper-wrapper {
        width: 1180px;
        height: auto;

        .swiper-slide {
          width: 360px;
          height: 360px;
          margin-bottom: 50px;
          display: flex;
          justify-content: center;
          align-items: center;

          &:nth-child(3) {
            margin-right: 0;
          }

          &:hover .new_img {
            transform: scale(1.01);
            transition: all 0.25s ease;
          }

          &:hover .new_txt {
            transform: scale(1.01);
            transition: all 0.25s ease;
          }

          .new_img {
            width: 360px;
            height: 360px;

            a img {
              width: 100%;
              height: 100%;
              object-fit: cover;
            }
          }

          .new_txt {
            width: 360px;
            height: 60px;
            background-color: #ffffff71;
            position: absolute;
            bottom: 0;
            left: 7px;
            z-index: 999;

            p {
              font-size: 12px;
              color: #191919;
              margin-top: 7px;
              padding: 2px 20px;
            }

            span {
              font-weight: bold;
              font-size: 13px;
              color: #191919;
              padding: 2px 20px;
            }
          }
        }
      }

      .swiper-scrollbar {
        width: 99%;
        height: 8px;
        margin: 0 auto;
        cursor: pointer;

        .swiper-scrollbar-drag {
          background-color: $color;
        }
      }
    }

    .newBtn_prev {
      position: absolute;
      top: 58%;
      left: 17%;
    }

    .newBtn_next {
      position: absolute;
      top: 58%;
      right: 17%;
    }

    .newBtn {
      color: #ccc;
      z-index: 999;
      width: 5px;
      height: 5px;
    }
  }


  /* ----------------------- best ----------------------- */
  .best {
    width: 100vw;
    height: auto;
    position: relative;

    h1 {
      text-align: center;
      padding: 10px 0;
      margin: 80px auto 55px;
    }

    .swiper_best {
      width: 1180px;
      height: auto;
      margin: 0 auto;

      .swiper-wrapper {
        width: 1180px;
        height: auto;
        display: flex;
        justify-content: center;
        align-items: center;


        /* .swiper-slide-visible:nth-child(2) {
          width: 620px;
          height: 458px;
          background-position: center;
          background-size: cover;

          .best_img {
            width: 620px;
            height: 458px;

            img {
              width: 100%;
              height: 100%;
              object-fit: cover;
            }
          }

          .best_txt {
            width: 620px;
            height: 65px;
            background-color: #ffffff71;
            position: absolute;
            bottom: 20px;
            left: 0;
            z-index: 999;

            p {
              font-size: 12px;
              color: #191919;
              margin-top: 7px;
              padding: 2px 20px;
            }

            span {
              font-weight: bold;
              font-size: 13px;
              color: #191919;
              padding: 2px 20px;
            }
          }
        } */

        .swiper-slide {
          width: 380px;
          height: 380px;
          //background-position: center;
          //background-size: cover;
          margin-bottom: 50px;

          .best_img {
            width: 380px;
            height: 380px;

            img {
              width: 100%;
              height: 100%;
              object-fit: cover;
            }
          }

          .best_txt {
            width: 380px;
            height: 65px;
            background-color: #ffffff71;
            position: absolute;
            bottom: 0;
            left: 0;
            z-index: 999;

            p {
              font-size: 12px;
              color: #191919;
              margin-top: 7px;
              padding: 2px 20px;
              display: block;
              white-space: nowrap;
              overflow: hidden;
              text-overflow: ellipsis;
            }

            span {
              font-weight: bold;
              font-size: 13px;
              color: #191919;
              padding: 2px 20px;
            }
          }
        }
      }
    }

    .bestBtn_prev {
      position: absolute;
      top: 55%;
      left: 17%;
    }

    .bestBtn_next {
      position: absolute;
      top: 55%;
      right: 17%;
    }

    .bestBtn {
      color: #ccc;
      z-index: 999;
      width: 5px;
      height: 5px;
    }
  }

  /* ----------------------- place ----------------------- */
  .issue_contents {
    width: 100vw;
    height: auto;

    .space_wrap {
      width: 1180px;
      height: auto;
      margin: 0 auto;

      h1 {
        text-align: center;
        padding: 10px 0;
        margin: 60px auto 50px;
      }

      .space_label {
        width: 800px;
        height: 50px;
        margin: 10px auto 20px;
        display: flex;
        justify-content: space-evenly;
        align-items: flex-start;

        li {
          width: 80px;
          font-weight: bold;
          text-align: center;
          cursor: pointer;

          &.on {
            padding-bottom: 8px;
            border-bottom: 5px solid $color;
            transition: all 0.1s;
          }
        }
      }

      .space_box {
        width: 1180px;
        height: 460px;
        display: flex;

        .img_left {
          width: 460px;
          height: 460px;
          margin-right: 20px;
          position: relative;

          .interior {
            width: 460px;
            height: 460px;
            position: absolute;
            top: 0;
            left: 0;
            display: none;

            &.ul_on {
              display: block;
            }

            li {
              width: 460px;
              height: 460px;
              display: none;

              &.li_on {
                display: block;
              }

              &:hover .left_txt {
                display: block;
              }

              .left_img {
                width: 460px;
                height: 460px;

                img {
                  width: 100%;
                  height: 100%;
                }
              }

              .left_txt {
                width: 460px;
                height: 80px;
                background-color: #ffffffb0;
                position: absolute;
                bottom: 0;
                left: 0;
                z-index: 999;
                //display: none;

                p {
                  font-size: 13px;
                  color: #191919;
                  margin-top: 7px;
                  padding: 9px 25px 5px;
                  display: block;
                  white-space: nowrap;
                  overflow: hidden;
                  text-overflow: ellipsis;
                }

                span {
                  font-weight: bold;
                  font-size: 14px;
                  color: #191919;
                  padding: 1px 25px;
                }
              }
            }
          }
        }

        .img_right {
          width: 720px;
          height: 460px;

          .product {
            width: 720px;
            height: 460px;
            display: none;

            &.right_on {
              display: block;
            }

            li {
              width: 220px;
              height: 220px;
              float: left;
              margin: 0 0 20px 20px;
              cursor: pointer;

              img {
                width: 100%;
                height: 100%;
              }
            }
          }
        }
      }
    }
  }

  /* ----------------------- brand ----------------------- */
  .brand_story {
    width: 100%;
    height: auto;
    margin: 140px auto 110px;
    background-color: #efe8daa9;
    display: flex;
    align-items: flex-start;

    .left_img {
      width: 50%;
      height: auto;

      img {
        width: 100%;
        height: 100%;
        display: block;
      }
    }

    .right_txt {
      width: 50%;
      height: auto;
      padding: 0 0 14px 80px;
      box-sizing: border-box;
      box-sizing: border-box;

      h1 {
        font-size: 26px;
        padding: 20px 0;
        box-sizing: border-box;
        margin: 100px 0 60px;
      }

      h2 {
        padding: 20px 0;
        box-sizing: border-box;
        margin-bottom: 20px;
      }

      p {
        font-size: 16px;
      }

      .button {
        width: 170px;
        height: 50px;
        background-color: #261C13;
        text-align: center;
        margin: 70px 0;
        cursor: pointer;

        &:hover {
          background-color: $color;
        }

        a {
          width: 180px;
          height: 50px;
          color: #EFE8DA;
          font-size: 14px;
          line-height: 48px;
        }
      }
    }
  }

  /* ----------------------- instagram ----------------------- */
  .instagram {
    width: 100%;
    height: auto;

    h1 {
      text-align: center;
      padding: 5px 0;
    }

    h4 {
      font-weight: normal;
      text-align: center;
      padding: 20px 0;
      margin-bottom: 40px;
    }

    .insta_box {
      width: 100%;
      height: 220px;
      overflow: hidden;
      position: relative;
      margin-bottom: 50px;

      .frame {
        width: 2400px;
        height: 220px;
        margin-left: -10px;
        position: absolute;
        top: 0;
        left: 0;

        li {
          width: 220px;
          height: 220px;
          padding: 0 10px;
          float: left;

          &:hover {
            transform: scale(1.01);
            transition: all 0.25s ease;
          }

          a img {
            width: 100%;
            height: 100%;
          }
        }
      }

      .ctr_btn {
        display: none;
      }

      .btn {
        display: none;
      }
    }

    .follow {
      margin-bottom: 110px;

      p.follow_insta {
        font-size: 16px;
        text-align: center;
        color: $color;
        padding: 10px 0;
      }
    }
  }
}


/* ----------------------- footer ----------------------- */
.footer {
  width: 100vw;
  height: 550px;
  background-color: #261C13;

  .footer_wrap {
    width: 1220px;
    height: auto;
    margin: 0 auto;
    position: relative;

    .cs {
      width: 350px;
      padding-top: 60px;

      h3 {
        color: #fff;
        padding-left: 2px;
      }

      h1 {
        color: $color;
      }

      p.kor {
        font-size: 14px;
        color: #fff;
        padding-left: 2px;
      }

    }

    .footer_logo {
      width: 100px;
      height: auto;
      display: inline-block;
      margin: 205px 0 20px 0;

      img {
        width: 100%;
        height: 100%;
      }
    }

    .copyright {
      color: #fff;
      font-size: 11px;
      padding: 5px 0;
    }

    .insta_icon {
      width: 35px;
      height: 35px;
      position: absolute;
      top: 50px;
      right: 0px;
      cursor: pointer;

      img {
        width: 100%;
        height: 100%;
      }
    }

    .footer_menu {
      width: 395px;
      height: auto;
      position: absolute;
      top: 138px;
      right: 0;
      display: flex;
      justify-content: space-between;

      .menu {
        p {
          color: #fff;
          font-size: 16px;
          font-weight: bold;
          margin-bottom: 10px;
        }

        ul li {
          padding: 2px 0;

          a {
            font-size: 13px;
            font-weight: bold;
            color: #fff;
          }
        }
      }
    }

    .business {
      width: 400px;
      height: 105px;
      position: absolute;
      top: 345px;
      right: -3px;

      .button {
        cursor: pointer;

        .btn i {
          color: #fff;
          position: absolute;
          top: 4px;
          left: 135px;
        }

        .btn_close {
          display: none;

          i {
            top: 2px;
          }
        }
      }

      .business_txt {
        h5 {
          font-size: 14px;
          font-weight: bold;
          color: #fff;
          margin-bottom: 20px;
        }

        .business_info {
          font-size: 13px;
          color: #fff;
          display: none;

          a {
            color: $color;
            cursor: pointer;
          }
        }
      }
    }
  }
}

/* ----------------------- top_btn ----------------------- */
.top_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #261C13;
  position: fixed;
  bottom: 50px;
  right: 50px;
