* {
  margin: 0;
  padding: 0;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #252117; //#332e22
}

body,
html {
  width: 100%;
  height: 100%;
  background-color: #f8f8f8;
  overflow-x: hidden;
  overflow-y: hidden;

}

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@100;300;400;500;700;900&family=Poppins:wght@300;400;500;600;700&display=swap');

.kor {
  font-family: 'Noto Sans KR', sans-serif;
}

.eng {
  font-family: 'Poppins', sans-serif;
}

$color : #FD8234;


/* ----------------------- main ----------------------- */
.main {
  width: 100%;
  height: auto;
  margin-top: 105px;

  .detailPage {
    display: flex;
    overflow: hidden;
    transition: 0.3s all ease;
    position: relative;

    .detail_content {
      width: 100%;
      height: calc(100vh - 105px);
      overflow-y: auto;
      overflow-x: hidden;
      margin-right: 760px;
      transition: 0.3s all ease;

      &::-webkit-scrollbar {
        width: 10px;
        height: auto;
        background-color: #eee;
      }

      &::-webkit-scrollbar-thumb {
        width: 10px;
        background-color: #FD8234;
      }

      .content_main {
        max-width: 1420px;
        width: calc(100vw - 760px);
        height: calc(100vh - 105px);
        margin: 0 auto;
        z-index: 9999;



        .swiper_detail {
          width: calc(100vw - 760px);
          height: calc(100vh - 105px);

          &:hover .swiperDetailBtn {
            visibility: visible;
          }

          .swiper-wrapper {
            width: calc(100vw - 760px);
            height: calc(100vh - 105px);

            .swiper-slide {
              display: flex;
              justify-content: center;
              align-items: center;

              img {
                display: block;
                width: 100%;
                height: 100%;
                object-fit: cover;
              }
            }
          }

          .detail_pagination {
            margin-bottom: 20px;

            .swiper-pagination {
              width: 10px;
              height: 10px;
            }

            .swiper-pagination-bullet-active {
              background-color: $color;
              border-radius: 10px;
              transform: scale(1.3);
              transition: 0.3s all ease;
            }
          }

          .swiperDetailBtn {
            visibility: hidden;
            margin-right: 50px;
          }

          .swiperDetailBtn:after {
            color: #ccc;
            padding: 10px;
            box-sizing: border-box;
            font-size: 20px;
            font-weight: bold;
          }

          .swiper-button-prev {
            margin-left: 35px;
          }
        }


        .tab_menu {
          width: calc(100vw - 760px);
          height: auto;
          position: relative;

          .tab_menu_wrap {
            position: sticky;
            top: 0;
            z-index: 999;
          }

          .menu_label {
            max-width: 1420px;
            width: calc(100vw - 760px);
            height: 85px;
            background-color: #fff;
            display: flex;
            justify-content: space-around;
            align-items: center;
            box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;

            li {
              width: calc((100vw - 760px) / 4);
              height: 85px;
              text-align: center;
              line-height: 85px;
              cursor: pointer;

              &.on {
                border-bottom: 5px solid #252117;
                box-sizing: border-box;
                transition: 0.3s all;
              }
            }
          }
        }

        .tab_box {
          width: calc(100vw - 760px);
          height: auto;

          .detail_box {
            width: calc(100vw - 760px);
            height: auto;
            background-color: #ccc;

            .tab_img {
              width: calc(100vw - 760px);
              height: auto;
              background-color: #ccc;
              transition: 0.4s all ease;

              img {
                display: block;
                width: 99%;
                height: 100%;
                object-fit: cover;
              }
            }

            .detail {
              display: none;
              transition: 0.4s all ease;

              img {
                display: block;
                width: 99%;
                height: 100%;
                object-fit: cover;
              }
            }

            .more_btn {
              width: calc(100vw - 760px);
              height: 85px;
              display: block;
              background-color: #fff;
              border: 2px solid #ccc;
              box-sizing: border-box;
              font-size: 18px;
              text-align: center;
              line-height: 85px;
              color: #252117;
              cursor: pointer;
              box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;

              i {
                padding: 5px;
              }
            }
          }

          .notice_box {
            width: 100%;
            height: auto;

            .notice_wrap {
              margin: 50px 30px 0 20px;
              padding: 10px 30px;

              .notice_list {
                padding: 25px 0;
              }

              h3 {
                font-size: 21px;
                margin-left: 56px;
              }

              .line {
                width: 940px;
                height: 3px;
                background-color: #191919;
                margin: 15px auto 18px;
              }

              p {
                font-size: 16px;
                font-weight: 500;
                padding: 4px 0;
                margin-left: 56px;
              }

              p.address {
                padding: 3px 5px;
              }
            }
          }


          .review_box {
            width: 100%;
            height: auto;
            background-color: #fff;
            margin-bottom: 10px;

            .review_box_wrap {
              position: relative;
              margin: 50px auto 0;
              padding: 70px 30px;

              .review_title {
                display: flex;
                justify-content: space-between;
                align-items: flex-end;

                h3 {
                  font-size: 21px;
                  margin-left: 82px;
                }

                p {
                  font-size: 14px;
                  margin-right: 85px;
                  cursor: pointer;
                }
              }

              .line {
                width: 950px;
                height: 3px;
                background-color: #191919;
                margin: 16px auto 18px;
              }

              .review_img {
                width: 900px;
                height: 120px;
                overflow: hidden;
                margin: 25px auto 40px;
                position: relative;

                .frame {
                  width: 1300px;
                  height: 120px;
                  position: absolute;
                  top: 0;
                  left: -5px;

                  li {
                    width: 120px;
                    height: 120px;
                    padding: 0 5px;
                    float: left;

                    a img {
                      width: 100%;
                      height: 100%;
                    }
                  }
                }
              }

              .review_btn {
                margin-bottom: 20px;
                cursor: pointer;

                .reviewBtn {
                  width: 20px;
                  height: 120px;
                  position: absolute;
                  top: 144px;
                  left: 105px;

                  i {
                    text-align: center;
                    color: #222;
                    line-height: 120px;
                  }
                }

                .next_btn {
                  left: 1045px;
                }
              }

              .review_list {
                width: 900px;
                height: auto;
                margin: 0 auto;

                ul {
                  li {
                    position: relative;
                    width: 100%;
                    height: 150px;
                    background-color: #efe8da9a;
                    border-radius: 2px;
                    padding: 15px 20px 0;
                    box-sizing: border-box;
                    margin-bottom: 22px;
                    box-shadow: rgba(0, 0, 0, 0.45) 0px 20px 15px -25px;
                    cursor: pointer;

                    >div {
                      display: flex;

                      p {
                        font-size: 14px;
                        padding: 4px 0 1px 10px;
                        white-space: nowrap;
                        overflow: hidden;
                        text-overflow: ellipsis;
                      }

                      &.user_info {

                        p {
                          margin-top: 5px;

                          &.id {
                            color: #3f3f3f;
                          }

                          &.date {
                            position: absolute;
                            top: 6px;
                            right: 22px;
                            font-size: 13px;
                            color: #3f3f3f;
                          }
                        }
                      }

                      &.purchase_option {
                        color: #3f3f3f;

                      }

                      &.review_content {
                        justify-content: space-between;
                        align-items: flex-start;

                        .content_txt {
                          //border: 1px solid black;
                          width: 600px;
                          height: 48px;
                          font-size: 15px;
                          overflow: hidden;
                          margin-top: 15px;
                        }

                        .content_img {
                          width: 90px;
                          height: 90px;
                          margin-top: -25px;

                          img {
                            width: 100%;
                            height: 100%;
                            object-fit: cover;
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }

          .question_box {
            width: 100%;
            height: 800px;
            background-color: #fff;

            .question_box_wrap {
              position: relative;
              margin: 0 auto;
              padding: 80px 30px;

              .question_title {
                display: flex;
                justify-content: space-between;
                align-items: flex-end;

                h3 {
                  font-size: 21px;
                  margin-left: 82px;
                }

                p {
                  font-size: 14px;
                  margin-right: 85px;
                  cursor: pointer;
                }
              }

              .line {
                width: 950px;
                height: 3px;
                background-color: #191919;
                margin: 16px auto 18px;
              }

              .que_category {
                width: 240px;
                height: 30px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                font-size: 14px;
                font-weight: 500;
                margin: 0 0 40px 82px;
                cursor: pointer;
              }

              .table_title {
                width: 930px;
                height: 30px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                font-size: 14px;
                font-weight: 500;
                margin: 20px 0 0 85px;

                div {
                  text-align: center;

                  &.title_user {
                    margin-left: 240px;
                  }

                }
              }

              .line_thin {
                width: 950px;
                height: 1px;
                background-color: #191919;
                margin: 12px auto 18px;
              }

              .inquire_list {
                ul {
                  li {
                    width: 940px;
                    height: 30px;
                    font-size: 14px;
                    font-weight: 500;
                    margin: 20px 0 20px 82px;

                    &:nth-child(1) {
                      margin-top: 25px;
                    }

                    .title {
                      margin-left: 10px;
                    }

                    .short {
                      display: flex;
                      justify-content: space-between;
                      align-items: center;

                      .status {
                        width: 50px;
                        height: 25px;
                        border-radius: 20px;
                        background-color: $color;
                        color: #fff;
                        text-align: center;
                        margin-right: 10px;
                      }

                      .user {
                        margin-left: 20px;
                      }
                    }

                    .answer_area {
                      width: 100%;
                      height: 240px;
                      background-color: #EFE8DA;
                      position: relative;
                      display: none;

                      .answer {
                        padding: 30px 0 0;
                        margin-top: 25px;
                        display: flex;

                        span {
                          width: 20px;
                          height: 20px;
                          text-align: center;
                          line-height: 20px;
                          padding: 20px;
                          font-size: 18px;
                          position: absolute;
                          top: 0;
                          right: 0;

                        }

                        div {
                          width: 50px;
                          height: 25px;
                          background-color: #fff;
                          border-radius: 20px;
                          text-align: center;
                          line-height: 25px;
                          margin: 0 15px 15px 20px;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }

    .content_side {
      position: absolute;
      top: 0;
      right: 0;
      width: 760px;
      height: 100%;
      background-color: #fff;
