/* ----------------------- shopping_history ----------------------- */
.history {

  .history_popup {
    position: fixed;
    right: -480px;
    bottom: 0;
    width: 480px;
    height: 100vh;
    background-color: #fff;
    transition: 0.4s all ease;
    z-index: 9999999999 !important;

    h4 {
      padding: 50px 30px 40px;
    }

    .history_hide {
      width: 25px;
      height: 25px;
      text-align: center;
      position: absolute;
      top: 45px;
      right: 25px;
      cursor: pointer;
    }

    p.today {
      font-size: 15px;
      padding: 0 30px;
      margin-bottom: 40px;
    }

    .shopping_history {
      display: block;

      .history_list {
        position: relative;
        width: 420px;
        height: 120px;
        background-color: #fcfcfc;
        border: none;
        margin: 0 auto 50px;
        display: block;
        cursor: pointer;

        .list_img {
          width: 100px;
          height: 120px;

          img {
            width: 100%;
            height: 100%;
          }
        }

        .list_txt {
          width: 250px;
          height: auto;
          position: absolute;
          top: 32px;
          right: 50px;

          .list_delete {
            width: 25px;
            height: 25px;
            text-align: center;
            position: absolute;
            top: -24px;
            left: 268px;
            display: none;
            color: #ccc;
          }

          p {
            width: 250px;
            text-align: left;
            font-size: 13px;
            display: block;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
          }

          span {
            position: absolute;
            top: 30px;
            font-size: 13px;
            font-weight: bold;
          }
        }
      }
    }

    .deleteAll {
      width: 120px;
      height: 45px;
      position: absolute;
      bottom: 60px;
      right: 30px;
      border-radius: 2px;
      background-color: #eee;
      cursor: pointer;

      &:hover {
        background-color: #252117;
      }

      &:hover p {
        color: #f8f8f8;
      }

      p {
        line-height: 45px;
        text-align: center;
        font-size: 13px;
      }
    }
  }
}
