  function pinkClick() {
    txt.innerHTML = '[필수] Peach & Deep Green',
      slideImg.src = './images/detail_PeachDeepGreen_gray.jpg'
  }

  function whiteClick() {
    txt.innerHTML = '[필수] White & Ice Blue',
      slideImg.src = './images/detail_WhiteIceBlue_gray.jpg'
  }
});



