sources.scss\\detail.scss:
* {
  margin: 0;
  padding: 0;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #252117; //#332e22
}

body,
html {
  width: 100%;
  height: 100%;
  background-color: #f8f8f8;
  overflow-x: hidden;
  overflow-y: hidden;

}

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@100;300;400;500;700;900&family=Poppins:wght@300;400;500;600;700&display=swap');

.kor {
  font-family: 'Noto Sans KR', sans-serif;
}

.eng {
  font-family: 'Poppins', sans-serif;
}

.show {
  display: block
}

.hide {
  display: none
}

$color : #FD8234;


/* ----------------------- main ----------------------- */
.main {
  width: 100%;
  height: auto;
  margin-top: 105px;

  .detailPage {
    width: 100%;
    height: auto;
    display: flex;
    overflow: hidden;
    transition: 0.3s all ease;
    position: relative;

    .detail_content {
      width: 100%;
      height: calc(100vh - 105px);
      overflow-y: auto;
      overflow-x: hidden;
      margin-right: 760px;
      transition: 0.3s all ease;

      &::-webkit-scrollbar {
        width: 10px;
        height: auto;
        background-color: #eee;
      }

      &::-webkit-scrollbar-thumb {
        width: 10px;
        background-color: #FD8234;
      }

      .content_main {
        max-width: 1420px;
        width: calc(100vw - 760px);
        height: calc(100vh - 105px);
        margin: 0 auto;
        z-index: 9999;



        .swiper_detail {
          width: calc(100vw - 760px);
          height: calc(100vh - 105px);

          &:hover .swiperDetailBtn {
            visibility: visible;
          }

          .swiper-wrapper {
            width: calc(100vw - 760px);
            height: calc(100vh - 105px);

            .swiper-slide {
              display: flex;
              justify-content: center;
              align-items: center;

              img {
                display: block;
                width: 100%;
                height: 100%;
                object-fit: cover;
              }
            }
          }

          .detail_pagination {
            margin-bottom: 20px;

            .swiper-pagination {
              width: 10px;
              height: 10px;
            }

            .swiper-pagination-bullet-active {
              background-color: $color;
              border-radius: 10px;
              transform: scale(1.3);
              transition: 0.3s all ease;
            }
          }

          .swiperDetailBtn {
            visibility: hidden;
            margin-right: 50px;
          }

          .swiperDetailBtn:after {
            color: #ccc;
            padding: 10px;
            box-sizing: border-box;
            font-size: 20px;
            font-weight: bold;
          }

          .swiper-button-prev {
            margin-left: 35px;
          }
        }


        .tab_menu {
          width: calc(100vw - 760px);
          height: auto;
          position: relative;

          .tab_menu_wrap {
            position: sticky;
            top: 0;
            z-index: 999;
          }

          .menu_label {
            max-width: 1420px;
            width: calc(100vw - 760px);
            height: 85px;
            background-color: #fff;
            display: flex;
            justify-content: space-around;
            align-items: center;
            box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;

            li {
              width: calc((100vw - 760px) / 4);
              height: 85px;
              text-align: center;
              line-height: 85px;

              &.on {
                border-bottom: 5px solid #252117;
                box-sizing: border-box;
                transition: 0.3s all;
              }

              a {
                width: calc((100vw - 760px) / 4);
                height: 85px;
                display: block;
                cursor: pointer;
              }
            }
          }
        }

        .tab_box {
          width: calc(100vw - 760px);
          height: auto;

          .detail_box {
            width: calc(100vw - 760px);
            height: auto;
            background-color: #ccc;

            .tab_img {
              width: calc(100vw - 760px);
              height: auto;
              background-color: #ccc;
              transition: 0.4s all ease;

              img {
                display: block;
                width: 99%;
                height: 100%;
                object-fit: cover;
              }
            }

            .detail {
              display: none;
              transition: 0.4s all ease;

              img {
                display: block;
                width: 99%;
                height: 100%;
                object-fit: cover;
              }
            }

            .more_btn {
              width: calc(100vw - 760px);
              height: 85px;
              display: block;
              background-color: #f8f8f8;
              border: 2px solid #ccc;
              box-sizing: border-box;
              font-size: 18px;
              font-weight: 500;
              text-align: center;
              line-height: 85px;
              color: #191919;
              cursor: pointer;
              box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;

              i {
                color: $color;
                padding: 5px;
              }
            }
          }

          .notice_box {
            width: 100%;
            height: auto;
            margin-top: 60px;

            .notice_wrap {
              margin: 0 auto;
              padding: 10px 30px;
              box-sizing: border-box;

              .notice_list {
                padding: 25px 0;
              }

              h3 {
                font-size: 21px;
                margin-left: 80px;
              }

              .line {
                width: 940px;
                height: 3px;
                background-color: #191919;
                margin: 15px auto 18px;
              }

              p {
                font-size: 16px;
                font-weight: 500;
                padding: 4px 0;
                margin-left: 80px;
              }

              p.address {
                padding: 3px 5px;
              }
            }
          }


          .review_box {
            width: 100%;
            height: auto;
            background-color: #fff;
            margin-bottom: 10px;

            .review_box_wrap {
              position: relative;
              margin: 50px auto 0;
              padding: 70px 30px;

              .review_title {
                display: flex;
                justify-content: space-between;
                align-items: flex-end;

                h3 {
                  font-size: 21px;
                  margin-left: 82px;
                }

                p {
                  font-size: 14px;
                  margin-right: 85px;
                  cursor: pointer;
                }
              }

              .line {
                width: 950px;
                height: 3px;
                background-color: #191919;
                margin: 16px auto 18px;
              }

              .review_img {
                width: 900px;
                height: 120px;
                overflow: hidden;
                margin: 25px auto 40px;
                position: relative;

                .frame {
                  width: 1300px;
                  height: 120px;
                  position: absolute;
                  top: 0;
                  left: -5px;

                  li {
                    width: 120px;
                    height: 120px;
                    padding: 0 5px;
                    float: left;

                    a img {
                      width: 100%;
                      height: 100%;
                    }
                  }
                }
              }

              .review_btn {
                margin-bottom: 20px;
                cursor: pointer;

                .reviewBtn {
                  width: 20px;
                  height: 120px;
                  position: absolute;
                  top: 144px;
                  left: 105px;

                  i {
                    text-align: center;
                    color: #222;
                    line-height: 120px;
                  }
                }

                .next_btn {
                  left: 1045px;
                }
              }

              .review_list {
                width: 900px;
                height: auto;
                margin: 0 auto;

                ul {
                  li {
                    position: relative;
                    width: 100%;
                    height: 150px;
                    background-color: #efe8da9a;
                    border-radius: 2px;
                    padding: 15px 20px 0;
                    box-sizing: border-box;
                    margin-bottom: 22px;
                    box-shadow: rgba(0, 0, 0, 0.45) 0px 20px 15px -25px;
                    cursor: pointer;

                    >div {
                      display: flex;

                      p {
                        font-size: 14px;
                        padding: 4px 0 1px 10px;
                        white-space: nowrap;
                        overflow: hidden;
                        text-overflow: ellipsis;
                      }

                      &.user_info {

                        p {
                          margin-top: 5px;

                          &.id {
                            color: #3f3f3f;
                          }

                          &.date {
                            position: absolute;
                            top: 6px;
                            right: 22px;
                            font-size: 13px;
                            color: #3f3f3f;
                          }
                        }
                      }

                      &.purchase_option {
                        color: #3f3f3f;

                      }

                      &.review_content {
                        justify-content: space-between;
                        align-items: flex-start;

                        .content_txt {
                          //border: 1px solid black;
                          width: 600px;
                          height: 48px;
                          font-size: 15px;
                          overflow: hidden;
                          margin-top: 15px;
                        }

                        .content_img {
                          width: 90px;
                          height: 90px;
                          margin-top: -25px;

                          img {
                            width: 100%;
                            height: 100%;
                            object-fit: cover;
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }

          .question_box {
            width: 100%;
            height: 800px;
            background-color: #fff;

            .question_box_wrap {
              position: relative;
              margin: 0 auto;
              padding: 80px 30px;

              .question_title {
                display: flex;
                justify-content: space-between;
                align-items: flex-end;

                h3 {
                  font-size: 21px;
                  margin-left: 82px;
                }

                p {
                  font-size: 14px;
                  margin-right: 85px;
                  cursor: pointer;
                }
              }

              .line {
                width: 950px;
                height: 3px;
                background-color: #191919;
                margin: 16px auto 18px;
              }

              .que_category {
                width: 240px;
                height: 30px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                font-size: 14px;
                font-weight: 500;
                margin: 0 0 40px 82px;
                cursor: pointer;
              }

              .table_title {
                width: 930px;
                height: 30px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                font-size: 14px;
                font-weight: 500;
                margin: 20px 0 0 85px;

                div {
                  text-align: center;

                  &.title_user {
                    margin-left: 240px;
                  }

                }
              }

              .line_thin {
                width: 950px;
                height: 1px;
                background-color: #191919;
                margin: 12px auto 10px;
              }

              .inquire_list {
                ul {
                  li {
                    width: 950px;
                    height: 55x;
                    line-height: 20px;
                    font-size: 14px;
                    font-weight: 500;
                    padding: 10px 0;
                    margin: 0 0 20px 75px;
                    cursor: pointer;

                    .title {
                      margin-left: 10px;
                    }

                    .short {
                      width: 950px;
                      height: 55px;
                      padding: 10px 0;
                      display: flex;
                      justify-content: space-between;
                      align-items: center;

                      &:hover {
                        background-color: rgb(249, 244, 238);
                      }

                      .title {
                        margin-left: 20px;
                      }

                      .status {
                        width: 50px;
                        height: 25px;
                        border-radius: 20px;
                        background-color: $color;
                        color: #fff;
                        text-align: center;
                        margin-right: 10px;
                      }

                      .user {
                        margin-left: 20px;
                      }
                    }

                    .answer_area {
                      width: 100%;
                      height: 240px;
                      background-color: #EFE8DA;
                      border-radius: 2px;
                      position: relative;
                      display: none;

                      .answer {
                        padding: 30px 0 0;
                        display: flex;

                        span {
                          width: 20px;
                          height: 20px;
                          text-align: center;
                          line-height: 20px;
                          padding: 20px;
                          font-size: 18px;
                          position: absolute;
                          top: 0;
                          right: 0;
                        }

                        div {
                          width: 50px;
                          height: 25px;
                          background-color: #fff;
                          border-radius: 20px;
                          text-align: center;
                          line-height: 25px;
                          margin: 0 15px 15px 20px;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }

    .content_side {
      position: absolute;
      top: 0;
      right: 0;
      width: 760px;
      height: 100%;
      background-color: #fff;
      overflow-y: auto;
      overflow-x: hidden;

      &::-webkit-scrollbar {
        width: 10px;
        height: auto;
        background-color: #eee;
      }

      &::-webkit-scrollbar-thumb {
        width: 10px;
        background-color: #FD8234;
      }

      .side_info {
        position: relative;
        width: 760px;
        height: auto;
        margin: 0;
        background-color: #fff;
        box-sizing: border-box;

        .side_info_wrap {
          width: 760px;
          height: calc(100vh - 105px);

          .product_info {
            width: 100%;
            height: 30%;

            h4 {
              padding: 38px 60px 0;
              box-sizing: border-box;
              font-weight: normal;
            }

            h3 {
              padding: 5px 60px 20px;
              font-size: 21px;
            }

            span {
              font-size: 21px;
              font-weight: bold;
              padding: 0 60px;
            }

            .select_option {
              position: absolute;
              top: 185px;
              left: 155px;

              h5 {
                color: #222;
              }
            }

            .color {
              width: auto;
              height: 30px;
              display: flex;
              align-items: center;
              padding: 10px 60px;
              margin: 22px 0 0px;

              div {
                width: 18px;
                height: 18px;
                border-radius: 50%;
                margin-right: 8px;
                border: 2px solid #eee;
                cursor: pointer;

                &:hover {
                  border: 2px solid #222;
                }

                &.on {
                  border: 2px solid #222;
                }

                &.butter {
                  background-color: #ffd18b;
                }

                &.pink {
                  background-color: #e4cfce;
                }

                &.white {
                  background-color: #fff;
                }
              }
            }
          }

          .option {
            position: relative;
            width: 100%;
            height: 30%;
            border-top: 2px solid #eee;
            box-sizing: border-box;

            h5 {
              color: #414040;
              padding: 25px 60px 8px;
              box-sizing: border-box;
            }

            .option_switch_box {
              width: 155px;
              height: 155px;
              margin: 5px 0 0 60px;

              .option_switch {
                display: flex;
                justify-content: flex-start;
                align-items: center;
                border: 2px solid #EEE;
                box-sizing: border-box;
                cursor: pointer;

                div {
                  width: 155px;
                  height: 155px;
                  display: none;

                  &.on {
                    display: block;
                  }

                  .option_img {
                    width: 155px;
                    height: 155px;

                    img {
                      display: block;
                      width: 100%;
                      height: 100%;
                      object-fit: cover;
                    }
                  }
                }
              }
            }
          }

          .option_txt {
            position: relative;

            .optionText {
              position: absolute;
              top: -90px;
              left: 250px;
              text-align: left;
              font-size: 12px;
              display: none;

              &.on {
                display: block;
              }

              p {
                font-size: 14px;
                font-weight: bold;
              }

              p.price {
                font-size: 15px;
                padding: 4px 0;
              }
            }
          }

          .option_color {
            width: auto;
            height: 30px;
            display: flex;
            align-items: center;
            margin: -30px 0 0 250px;

            div {
              width: 15px;
              height: 15px;
              border-radius: 50%;
              margin-right: 5px;
              border: 2px solid #eee;
              cursor: pointer;

              &:hover {
                border: 2px solid #222;
              }

              &.on {
                border: 2px solid #222;
              }

              &.butter {
                background-color: #ffd18b;
              }

              &.green {
                background-color: darkgreen;
              }

              &.orange {
                background-color: orangered;
              }

              &.black {
                background-color: #000;
              }
            }
          }

          .buying_info {
            width: 100%;
            height: 19%;
            padding: 33px 0 0 65px;
            box-sizing: border-box;
            border-top: 2px solid #eee;
            font-size: 15px;

            .shipment {
              width: 100%;
              height: auto;
              display: flex;

              p {
                font-weight: bold;
                margin-right: 50px;
              }

              ul li {
                &:nth-child(2) {
                  padding: 2px 0;
                }
              }
            }

            .card_benefit {
              width: 100%;
              height: 46px;
              display: flex;
              align-items: center;
              margin-top: 10px;

              p {
                line-height: 46px;
                font-weight: bold;
                margin-right: 35px;
              }

              .more_mark {
                width: 30px;
                height: 30px;
                font-size: 17px;
                cursor: pointer;

                i {
                  color: $color;
                }
              }

              .card_popup {
                width: 255px;
                height: 310px;
                background-color: #f8f8f8;
                color: #191919;
                border-radius: 15px 15px 15px 0;
                position: absolute;
                top: 26%;
                left: 21.5%;
                padding: 20px 5px;
                box-sizing: border-box;
                box-shadow: rgba(0, 0, 0, 0.07) 0px 1px 1px, rgba(0, 0, 0, 0.07) 0px 2px 2px, rgba(0, 0, 0, 0.07) 0px 4px 4px, rgba(0, 0, 0, 0.07) 0px 8px 8px, rgba(0, 0, 0, 0.07) 0px 16px 16px;
                display: none;


                h3 {
                  display: inline-block;
                  font-size: 16px;
                  padding: 20px 34px;
                }

                h4 {
                  display: inline-block;
                  padding: 0px 34px 10px;
                }

                .line_thin {
                  width: 75%;
                  height: 1px;
                  background-color: #191919;
                  margin: 5px auto 12px;
                }

                ul {

                  li {
                    width: 97%;
                    height: 35px;
                    display: flex;
                    justify-content: space-evenly;
                    margin-left: 20px;

                    p {
                      font-size: 13px;

                      &:first-child {
                        color: $color;
                      }
                    }
                  }
                }
              }
            }
          }

          .allPrice {
            width: 100%;
            height: 11%;
            display: flex;
            justify-content: flex-start;
            //justify-content: space-between;
            align-items: flex-end;
            padding-left: 60px;
            border-top: 2px solid #eee;
            box-sizing: border-box;
            line-height: 88px;

            p {
              font-size: 16px;
              font-weight: 500;
            }

            .count {
              display: flex;
              justify-content: flex-start;
              align-items: baseline;
              margin: 29px 45px 0 60px;

              input {
                width: 35px;
                height: 35px;
                border: none;
                font-size: 16px;
                font-weight: bold;
                text-align: center;
                cursor: pointer;

                &:nth-child(2) {
                  margin-left: 140px;
                }

                &:nth-child(3) {
                  border: 2px solid #eee;
                  box-sizing: border-box;
                }

                &:nth-child(5) {
                  border: none;
                  width: 100px;
                  height: 50px;
                  font-size: 18px;
                  font-weight: bold;
                }

                &.hover {
                  background-color: #eee;
                }

                &.hover:hover {
                  color: $color;
                }
              }

              p.won {
                font-size: 18px;
                font-weight: bold;
                margin-left: -10px;
              }
            }

            .finalCount {
              display: flex;
              align-items: baseline;

              input {
                width: 30px;
                height: 30px;
                border-radius: 50%;
                background-color: $color;
                margin: 10px 5px 10px 15px;
                line-height: 30px;
                font-size: 18px;
                font-weight: bold;
                border: none;
              }

              p {
                font-size: 18px;
                font-weight: bold;
                margin-left: 2px;
                line-height: 28px;
              }
            }
          }

          .shopping_button {
            width: 100%;
            height: 10%;
            display: flex;

            div {
              width: 50%;
              height: 85px;
              font-weight: 22px;
              font-weight: 500;
              letter-spacing: 0.5px;
              text-align: center;
              line-height: 85px;
              border: 1px solid #EFE8DA;
              box-sizing: border-box;
              box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
              /* box-shadow: rgba(50, 50, 93, 0.25) 0px 6px 12px -2px, rgba(0, 0, 0, 0.3) 0px 3px 7px -3px; */
              cursor: pointer;

              &.buying_btn {
                background-color: #EFE8DA;
                color: #191919;
              }

              &.buying_btn:hover {
                background-color: #252117;
                border: 1px solid #252117;
                color: $color;
              }

              &.cart_btn {
                color: #191919;
              }

              &.cart_btn:hover {
                background-color: #252117;
                border: 1px solid #252117;
                color: $color;
              }
            }
          }
        }
      }
    }


    .side_notice {
      width: 100%;
      height: auto;
      padding-bottom: 65px;

      div {
        width: 100%;
        height: auto;
        position: relative;

        .title {
          width: 100%;
          height: 80px;
          background-color: #fff;
          border-bottom: 1px solid #eee;
          box-shadow: rgba(0, 0, 0, 0.18) 0px 2px 4px;
          cursor: pointer;

          h4 {
            line-height: 80px;
            padding-left: 60px;
            font-weight: 500;
          }
        }

        .openClose {
          position: absolute;
          top: 30px;
          left: 680px;
          cursor: pointer;

          div {
            &.minus_btn {
              display: none;
            }

            i {
              font-size: 14px;
              color: #191919;
            }
          }
        }

        .hide {
          width: 100%;
          height: auto;
          padding-bottom: 20px;
          box-sizing: border-box;
          background-color: #f1f1f1;
          box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 1px 3px 1px;
          display: none;

          p {
            padding: 30px 60px;
          }
        }
      }
    }
  }
}


/* ----------------------- top_btn ----------------------- */
.top_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #191919;
  position: fixed;
  top: 185px;
  right: 25px;
  text-align: center;
  z-index: 999;
  cursor: pointer;

  i {
    text-align: center;
    line-height: 40px;
    color: #fff;
  }
}

.history_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #191919;
  position: fixed;
  top: 135px;
  right: 25px;
  text-align: center;
  z-index: 999;
  cursor: pointer;


  i {
    text-align: center;
    line-height: 40px;
    color: #fff;
  }
}


/* ----------------------- shopping_history ----------------------- */
.history {

  .history_popup {
    position: fixed;
    right: -480px;
    bottom: 0;
    width: 480px;
    height: 100vh;
    background-color: #fff;
    transition: 0.4s all ease;
    z-index: 9999999999 !important;

    h4 {
      padding: 50px 30px 40px;
    }

    .history_hide {
      width: 25px;
      height: 25px;
      text-align: center;
      position: absolute;
      top: 45px;
      right: 25px;
      cursor: pointer;
    }

    p.today {
      font-size: 15px;
      padding: 0 30px;
      margin-bottom: 40px;
    }

    .shopping_history {
      display: block;

      .history_list {
        position: relative;
        width: 420px;
        height: 120px;
        background-color: #fcfcfc;
        border: none;
        margin: 0 auto 50px;
        display: block;
        cursor: pointer;

        .list_img {
          width: 100px;
          height: 120px;

          img {
            width: 100%;
            height: 100%;
          }
        }

        .list_txt {
          width: 250px;
          height: auto;
          position: absolute;
          top: 32px;
          right: 50px;

          .list_delete {
            width: 25px;
            height: 25px;
            text-align: center;
            position: absolute;
            top: -24px;
            left: 268px;
            display: none;
            color: #ccc;
          }

          p {
            width: 250px;
            text-align: left;
            font-size: 13px;
            display: block;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
          }

          span {
            position: absolute;
            top: 30px;
            font-size: 13px;
            font-weight: bold;
          }
        }
      }
    }

    .deleteAll {
      width: 120px;
      height: 45px;
      position: absolute;
      bottom: 60px;
      right: 30px;
      border-radius: 2px;
      background-color: #eee;
      cursor: pointer;

      &:hover {
        background-color: #252117;
      }

      &:hover p {
        color: #f8f8f8;
      }

      p {
        line-height: 45px;
        text-align: center;
        font-size: 13px;
      }
    }
  }
}
