.main .detailPage .content_side .side_info .side_info_wrap .allPrice .priceCount p.won {
  font-size: 18px;
  font-weight: bold;
  margin-left: -10px;
}
.main .detailPage .content_side .side_info .side_info_wrap .allPrice .finalAmount {
  display: flex;
  align-items: baseline;
}
.main .detailPage .content_side .side_info .side_info_wrap .allPrice .finalAmount input {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #FD8234;
  margin: 10px 5px 10px 15px;
  line-height: 30px;
  font-size: 18px;
  font-weight: bold;
  border: none;
}
.main .detailPage .content_side .side_info .side_info_wrap .allPrice .finalAmount p {
  font-size: 18px;
  font-weight: bold;
  margin-left: 2px;
  line-height: 28px;
}
.main .detailPage .content_side .side_info .side_info_wrap .shopping_button {
  width: 100%;
  height: 10%;
  display: flex;
}
.main .detailPage .content_side .side_info .side_info_wrap .shopping_button div {
  width: 50%;
  height: 85px;
  font-weight: 22px;
  font-weight: 500;
  letter-spacing: 0.5px;
  text-align: center;
  line-height: 85px;
  border: 1px solid #EFE8DA;
  box-sizing: border-box;
  box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
  /* box-shadow: rgba(50, 50, 93, 0.25) 0px 6px 12px -2px, rgba(0, 0, 0, 0.3) 0px 3px 7px -3px; */
  cursor: pointer;
}
.main .detailPage .content_side .side_info .side_info_wrap .shopping_button div.buying_btn {
  background-color: #EFE8DA;
  color: #191919;
}
.main .detailPage .content_side .side_info .side_info_wrap .shopping_button div.buying_btn:hover {
  background-color: #252117;
  border: 1px solid #252117;
  color: #FD8234;
}
.main .detailPage .content_side .side_info .side_info_wrap .shopping_button div.cart_btn {
  color: #191919;
}
.main .detailPage .content_side .side_info .side_info_wrap .shopping_button div.cart_btn:hover {
  background-color: #252117;
  border: 1px solid #252117;
  color: #FD8234;
}
.main .detailPage .content_side .side_notice {
  width: 100%;
  height: auto;
  padding-bottom: 65px;
}
.main .detailPage .content_side .side_notice div {
  width: 100%;
  height: auto;
  position: relative;
}
.main .detailPage .content_side .side_notice div .title {
  width: 100%;
  height: 80px;
  background-color: #fff;
  border-bottom: 1px solid #eee;
  box-shadow: rgba(0, 0, 0, 0.18) 0px 2px 4px;
  cursor: pointer;
}
.main .detailPage .content_side .side_notice div .title h4 {
  line-height: 80px;
  padding-left: 60px;
  font-weight: 500;
}
.main .detailPage .content_side .side_notice div .openClose {
  position: absolute;
  top: 30px;
  left: 680px;
  cursor: pointer;
}
.main .detailPage .content_side .side_notice div .openClose div {
  display: none;
}
.main .detailPage .content_side .side_notice div .openClose div.minus_btn {
  position: absolute;
  top: 0px;
}
.main .detailPage .content_side .side_notice div .openClose div.show {
  display: block;
}
.main .detailPage .content_side .side_notice div .openClose div i {
  font-size: 14px;
  color: #191919;
}
.main .detailPage .content_side .side_notice div .hide {
  width: 100%;
  height: auto;
  padding-bottom: 20px;
  box-sizing: border-box;
  background-color: #f1f1f1;
  box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 1px 3px 1px;
  display: none;
}
.main .detailPage .content_side .side_notice div .hide.show {
  display: block;
}
.main .detailPage .content_side .side_notice div .hide p {
  padding: 30px 60px;
}

/* ----------------------- top_btn ----------------------- */
.top_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #191919;
  position: fixed;
  top: 185px;
  right: 25px;
  text-align: center;
  z-index: 999;
  cursor: pointer;
  opacity: 0;
}
.top_btn i {
  text-align: center;
  line-height: 40px;
  color: #fff;
}

.history_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #191919;
  position: fixed;
  top: 135px;
  right: 25px;
  text-align: center;
  z-index: 999;
  cursor: pointer;
}
.history_btn i {
  text-align: center;
  line-height: 40px;
  color: #fff;
}

/* ----------------------- shopping_history ----------------------- */
.history .history_popup {
  position: fixed;
  right: 0;
  bottom: 0;
  width: 480px;
  height: 100vh;
  background-color: #fff;
  transition: 0.4s all ease;
  z-index: 9999999999 !important;
  box-shadow: rgba(149, 157, 165, 0.2) 0px 8px 24px;
  display: none;
}
.history .history_popup h4 {
  padding: 50px 30px 40px;
}
.history .history_popup .history_hide {
  width: 25px;
  height: 25px;
  text-align: center;
  position: absolute;
  top: 45px;
  right: 25px;
  cursor: pointer;
}
.history .history_popup p.today {
  font-size: 15px;
  padding: 0 30px;
  margin-bottom: 40px;
}
.history .history_popup .shopping_history {
  display: block;
}
.history .history_popup .shopping_history .history_list {
  position: relative;
  width: 420px;
  height: 120px;
  background-color: #fcfcfc;
  border: none;
  margin: 0 auto 50px;
  display: block;
  cursor: pointer;
}
.history .history_popup .shopping_history .history_list .list_img {
  width: 100px;
  height: 120px;
}
.history .history_popup .shopping_history .history_list .list_img img {
  width: 100%;
  height: 100%;
}
.history .history_popup .shopping_history .history_list .list_txt {
  width: 250px;
  height: auto;
  position: absolute;
  top: 32px;
  right: 50px;
}
.history .history_popup .shopping_history .history_list .list_txt .list_delete {
  width: 25px;
  height: 25px;
  text-align: center;
  position: absolute;
  top: -24px;
  left: 268px;
  display: none;
  color: #ccc;
}
.history .history_popup .shopping_history .history_list .list_txt p {
  width: 250px;
  text-align: left;
  font-size: 13px;
  display: block;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.history .history_popup .shopping_history .history_list .list_txt span {
  position: absolute;
  top: 30px;
  font-size: 13px;
  font-weight: bold;
}
.history .history_popup .deleteAll {
  width: 120px;
  height: 45px;
  position: absolute;
  bottom: 60px;
  right: 30px;
  border-radius: 2px;
  background-color: #eee;
  cursor: pointer;
}
.history .history_popup .deleteAll:hover {
  background-color: #252117;
}
.history .history_popup .deleteAll:hover p {
  color: #f8f8f8;
}
.history .history_popup .deleteAll p {
  line-height: 45px;
  text-align: center;
  font-size: 13px;
}

sources.scss\\detail.scss:
* {
  margin: 0;
  padding: 0;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #252117; //#332e22
}

body,
html {
  width: 100%;
  height: 100%;
  background-color: #f8f8f8;

  &::-webkit-scrollbar {
    width: 0px;
    height: 0px;
    background-color: #eee;
  }

}

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@100;300;400;500;700;900&family=Poppins:wght@300;400;500;600;700&display=swap');

.kor {
  font-family: 'Noto Sans KR', sans-serif;
}

.eng {
  font-family: 'Poppins', sans-serif;
}

.show {
  display: block
}

.hide {
  display: none
}

$color : #FD8234;


/* ----------------------- main ----------------------- */
.main {
  width: 100%;
  height: auto;
  margin-top: 105px;

  .detailPage {
    width: 100%;
    height: auto;
    display: flex;
    overflow: hidden;
    transition: 0.3s all ease;
    position: relative;

    .detail_content {
      width: calc(100vw - 760px);
      height: auto;
      overflow-y: auto;
      overflow-x: auto;
      margin-right: 760px;
      transition: 0.3s all ease;
      visibility: visible;

      &::-webkit-scrollbar {
        width: 10px;
        height: 0px;
        background-color: #eee;
      }

      &::-webkit-scrollbar-thumb {
        width: 10px;
        background-color: #FD8234;
      }

      .content_main {
        max-width: 1420px;
        width: calc(100vw - 760px);
        height: calc(100vh - 105px);
        margin: 0 auto;
        z-index: 9999;

        .swiper_detail {
          width: 100%;
          height: calc(100vh - 105px);

          &:hover .swiperDetailBtn {
            visibility: visible;
          }

          .swiper-wrapper {
            width: 100%;
            height: 100%;

            .swiper-slide {
              display: flex;
              justify-content: center;
              align-items: center;

              img {
                display: block;
                width: 100%;
                height: 100%;
                object-fit: cover;
              }
            }
          }

          .detail_pagination {
            margin-bottom: 20px;

            .swiper-pagination {
              width: 10px;
              height: 10px;
            }

            .swiper-pagination-bullet-active {
              background-color: $color;
              border-radius: 10px;
              transform: scale(1.3);
              transition: 0.3s all ease;
            }
          }

          .swiperDetailBtn {
            visibility: hidden;
            margin-right: 50px;
          }

          .swiperDetailBtn:after {
            color: $color;
            padding: 10px;
            box-sizing: border-box;
            font-size: 20px;
            font-weight: bold;
          }

          .swiper-button-prev {
            margin-left: 35px;
          }
        }

        .tabMenu {
          width: 100%;
          height: auto;
          position: relative;

          .tabMenu_wrap {
            height: 90px;
            position: absolute;
            top: 0;
            left: 0;
            z-index: 999;

            .menu_label {
              width: 100%;
              height: 90px;
              background-color: #fff;
              display: flex;
              justify-content: space-around;
              align-items: center;
              box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;

              li {
                width: calc((100vw - 760px) / 4);
                height: 90px;
                text-align: center;
                line-height: 90px;

                &.on {
                  border-bottom: 5px solid $color;
                  box-sizing: border-box;
                  transition: 0.3s all;
                }

                a {
                  width: calc((100vw - 760px) / 4);
                  height: 90px;
                  display: block;
                  font-weight: 500;
                  font-size: 15px;
                  cursor: pointer;
                }
              }
            }
          }

          .tabMenu_header {
            position: relative;
            width: 100vw;
            height: 90px;
            opacity: 0;

            .tabMenu_headerWrap {
              width: 100%;
              height: 105px;
              position: fixed;
              top: 0;
              left: 0;
              background-color: #f8f8f8;
              box-shadow: rgba(0, 0, 0, 0.09) 0px 3px 12px;
              z-index: 99999999;

              .logo {
                width: 55px;
                height: auto;
                padding-top: 21px;
                box-sizing: border-box;
                margin: 0px 0px 0px 60px;
                cursor: pointer;

                .logo a {
                  width: 55px;
                  height: auto;
                  display: block;
                }

                img {
                  width: 100%;
                }
              }

              .tabMenu_label {
                .lnb_main {
                  width: 665px;
                  height: 105px;
                  display: flex;
                  justify-content: space-between;
                  align-items: flex-start;
                  position: absolute;
                  top: 57px;
                  left: 200px;

                  li {
                    height: 100px;
                    padding: 0 25px;

                    a {
                      font-size: 15px;
                      font-weight: bold;
                      cursor: pointer;
                    }

                    &:hover a {
                      color: $color;
                    }
                  }
                }

                .lnb_detail {
                  position: relative;
                  width: 100vw;
                  z-index: 9;

                  .shop_detail_bg {
                    width: 520px;
                    height: calc(100vh - 105px);
                    position: absolute;
                    top: 14px;
                    left: -869px;
                    transition: all 0.3s;

                    .shop_detail {
                      width: 520px;
                      height: calc(100vh - 105px);
                      background-color: #F8F6F4;

                      & li:hover {
                        background-color: #EFE8DA;
                      }

                      & li:hover a {
                        color: $color;
                      }

                      li {
                        width: 100%;
                        height: 72px;
                        border-bottom: 1px solid #eee;
                        padding: 12px 0 0 70px;
                        box-sizing: border-box;
                        line-height: 50px;
                      }

                      li a {
                        display: block;
                        width: 100%;
                        height: 72px;
                        font-size: 13px;
                        font-weight: bold;
                        cursor: pointer;
                      }

                      & li:nth-child(1) {
                        width: 50%;
                        height: 72px;
                        display: inline-block;
                      }

                      & li:nth-child(2) {
                        width: 50%;
                        height: 72px;
                        float: left;
                      }
                    }
                  }

                  .story_detail_bg {
                    width: 100vw;
                    height: 60px;
                    position: absolute;
                    top: 16px;
                    left: 0;
                    display: none;

                    ul.story_detail {
                      width: 100vw;
                      height: 60px;
                      background-color: #EFE8DA;
                      display: flex;
                      transition: all 0.5s;


                      & li:hover a {
                        color: $color;
                      }

                      li {
                        padding: 17px 10px 0;
                        cursor: pointer;
                      }

                      li a {
                        font-size: 13px;
                        font-weight: bold;

                      }

                      li:nth-child(1) {
                        margin: 0 45px 0 305px;
                      }
                    }
                  }

                  .support_detail_bg {
                    width: 100vw;
                    height: 60px;
                    position: absolute;
                    top: 16px;
                    left: 0;
                    transition: all 0.5s;
                    display: none;

                    ul.support_detail {
                      width: 100vw;
                      height: 60px;
                      background-color: #EFE8DA;
                      display: flex;
                      transition: all 0.5s;

                      & li:hover a {
                        color: $color;
                      }

                      li {
                        padding: 17px 10px 0;
                        margin-right: 45px;
                      }

                      li a {
                        font-size: 13px;
                        font-weight: bold;
                      }

                      li:nth-child(1) {
                        margin-left: 645px;
                      }
                    }
                  }
                }

                .headMenu_label {
                  width: calc(100vw - 760px);
                  height: 60px;
                  background-color: #fff;
                  box-shadow: rgba(50, 50, 93, 0.25) 0px 2px 5px -1px, rgba(0, 0, 0, 0.3) 0px 1px 3px -1px;
                  display: flex;
                  position: absolute;
                  top: 105px;
                  left: 0;

                  li {
                    width: calc((100vw - 760px) / 4);
                    height: 60px;

                    &.on {
                      border-bottom: 5px solid $color;
                      box-sizing: border-box;
                      transition: 0.3s all;
                    }

                    a {
                      width: 100%;
                      height: 100%;
                      text-align: center;
                      line-height: 60px;
                      display: block;
                      font-size: 13px;
                      font-weight: bold;
                      cursor: pointer;
                    }

                    a:hover {
                      color: #FD8234;
                    }
                  }
                }
              }

              .gnb {
                ul {
                  width: 400px;
                  height: 50px;
                  display: flex;
                  justify-content: space-between;
                  align-items: flex-start;
                  position: absolute;
                  top: 57px;
                  right: 55px;

                  li {
                    font-size: 15px;
                    font-weight: bold;
                    cursor: pointer;

                    &.search {
                      width: 165px;
                      height: 25px;
                      background-color: $color;
                      border-radius: 20px;

                      input {
                        width: 165px;
                        height: 25px;
                        padding: 12px 15px;
                        box-sizing: border-box;
                        color: #f8f8f8;
                        background-color: $color;
                        border-radius: 20px;
                        border: none;
                        position: relative;
                      }

                      i {
                        font-size: 13px;
                        color: #f8f8f8;
                        position: absolute;
                        top: 6px;
                        right: 246px;
                      }
                    }
                  }

                  li:hover a {
                    color: $color;
                  }

                }
              }
            }
          }


          .tab_box {
            width: 100%;
            height: auto;

            .detail_box {
              width: 100%;
              height: auto;
              background-color: #ccc;

              .tab_img {
                width: 100%;
                height: auto;
                background-color: #ccc;
                transition: 0.4s all ease;

                img {
                  display: block;
                  width: 99%;
                  height: 100%;
                  object-fit: cover;
                }
              }

              .detail {
                display: none;
                transition: 0.4s all ease;

                img {
                  display: block;
                  width: 99%;
                  height: 100%;
                  object-fit: cover;
                }
              }

              .more_btn {
                width: 100%;
                height: 85px;
                display: block;
                background-color: #f8f8f8;
                border: 2px solid #ccc;
                box-sizing: border-box;
                font-size: 18px;
                font-weight: 500;
                text-align: center;
                line-height: 85px;
                color: #191919;
                cursor: pointer;
                box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;

                i {
                  color: $color;
                  padding: 5px;
                }
              }
            }

            .notice_box {
              width: 100%;
              height: auto;

              .notice_wrap {
                width: 940px;
                height: 100%;
                margin: 0 auto;
                padding: 70px 0;
                box-sizing: border-box;

                .notice_list {
                  padding: 25px 0;
                }

                h3 {
                  font-size: 21px;
                  margin-left: 3px;
                }

                .line {
                  width: 940px;
                  height: 3px;
                  background-color: #191919;
                  margin: 15px auto 18px;
                }

                p {
                  font-size: 16px;
                  font-weight: 500;
                  padding: 4px 0;
                  margin-left: 5px;
                }

                p.address {
                  padding: 3px 5px;
                }
              }
            }


            .review_box {
              width: 100%;
              height: auto;
              background-color: #fff;
              margin: 50px 0 0;

              .review_box_wrap {
                position: relative;
                width: 940px;
                height: 100%;
                margin: 0 auto;
                padding: 80px 0 20px;
                box-sizing: border-box;

                .review_title {
                  display: flex;
                  justify-content: space-between;
                  align-items: flex-end;

                  h3 {
                    font-size: 21px;
                    margin-left: 15px;
                  }

                  p {
                    font-size: 14px;
                    margin-right: 85px;
                    cursor: pointer;
                  }
                }

                .line {
                  width: 940px;
                  height: 3px;
                  background-color: #191919;
                  margin: 16px auto 18px;
                }

                .review_img {
                  width: 905px;
                  height: 120px;
                  overflow: hidden;
                  margin: 25px auto;
                  position: relative;

                  .frame {
                    width: 1300px;
                    height: 120px;
                    position: absolute;
                    top: 0;
                    left: 0;

                    li {
                      width: 120px;
                      height: 120px;
                      padding: 0 5px;
                      float: left;

                      a img {
                        width: 100%;
                        height: 100%;
                      }
                    }
                  }
                }

                .review_btn {
                  margin-bottom: 20px;
                  cursor: pointer;

                  .reviewBtn {
                    width: 20px;
                    height: 120px;
                    position: absolute;
                    top: 145px;
                    left: 0px;

                    i {
                      text-align: center;
                      color: #222;
                      line-height: 120px;
                    }
                  }

                  .next_btn {
                    left: 935px;
                  }
                }

                .review_write {
                  width: 940px;
                  height: 140px;
                  margin: 15px auto 255px;
                  position: relative;

                  h4 {
                    padding: 15px 0 20px;
                    margin: 25px 0 0 20px;
                    color: $color;
                  }

                  div {
                    width: 900px;
                    margin: 0 auto 8px;
                    font-size: 14px;
                    font-weight: 500;

                    input {
                      margin: 5px 0 2px;
                    }

                    &.review_user {
                      display: flex;
                      flex-direction: column;
                      align-items: flex-start;
                    }

                    & input {
                      width: 680px;
                      height: 20px;
                      border: none;
                      border-bottom: 1px solid #222;
                      line-height: 20px;
                    }

                    &.review_title {
                      display: flex;
                      flex-direction: column;
                      align-items: flex-start;

                      input {
                        width: 680px;
                      }
                    }

                    &.review_select {
                      position: relative;

                      #SNOWMAN22 {
                        width: 200px;
                        height: 30px;
                        position: absolute;
                        padding: 0 5px;
                        box-sizing: border-box;
                        top: -40px;
                        right: 0;
                      }

                      .inputImage {
                        width: 200px;
                        height: 30px;
                        position: absolute;
                        top: -100px;
                        right: 0;
                        border: none;
                      }
                    }

                    &.review_textarea {
                      margin-top: 15px;

                      input {
                        width: 900px;
                        height: 120px;
                        padding: 20px;
                        border: none;
                        box-sizing: border-box;
                        margin: 5px 0 10px;
                        background-color: #f5f5f5;
                      }
                    }

                    &.review_save {
                      position: relative;

                      .save_btn {
                        position: absolute;
                        bottom: -25px;
                        right: 0;
                        width: 58px;
                        height: 30px;
                        line-height: 31px;
                        border-radius: 5px;
                        border: none;
                        font-weight: 700;
                        background-color: $color;
                        color: #EFE8DA;
                        cursor: pointer;
                      }
                    }
                  }
                }


                .review_list {
                  width: 900px;
                  height: auto;
                  margin: 40px auto;

                  ul {
                    li {
                      position: relative;
                      width: 100%;
                      height: 170px;
                      background-color: #efe8da9a;
                      border-radius: 2px;
                      padding: 15px 20px 0;
                      box-sizing: border-box;
                      margin-bottom: 22px;
                      box-shadow: rgba(0, 0, 0, 0.45) 0px 20px 15px -25px;
                      cursor: pointer;

                      >div {
                        display: flex;

                        p {
                          font-size: 14px;
                          padding: 4px 0 1px 10px;
                          white-space: wrap;
                          //overflow: hidden;
                          text-overflow: ellipsis;
                        }

                        &.user_info {

                          p {
                            margin-top: 5px;

                            &.userId {
                              color: #3f3f3f;
                            }

                            &.revDate {
                              position: absolute;
                              top: 6px;
                              right: 22px;
                              font-size: 13px;
                              color: #3f3f3f;
                            }
                          }
                        }

                        &.purchaseOption {
                          color: #3f3f3f;

                        }

                        &.reviewContent {
                          justify-content: space-between;
                          align-items: flex-start;

                          .reviewTxt {
                            //border: 1px solid black;
                            width: 640px;
                            height: 48px;
                            font-size: 15px;
                            overflow: hidden;
                            margin-top: 15px;
                          }

                          .contentImg {
                            width: 90px;
                            height: 90px;
                            margin-top: -25px;

                            img {
                              width: 100%;
                              height: 100%;
                              object-fit: cover;
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
            }

            .question_box {
              width: 100%;
              height: auto;
              background-color: #fff;
              margin-top: 5px;

              .question_box_wrap {
                position: relative;
                width: 940px;
                height: auto;
                margin: 0 auto;
                padding: 80px 0 20px;
                box-sizing: border-box;

                .question_title {

                  h3 {
                    font-size: 21px;
                    margin-left: 15px;
                  }

                  p {
                    font-size: 14px;
                    margin-right: 85px;
                    cursor: pointer;
                  }
                }

                .line {
                  width: 940px;
                  height: 3px;
                  background-color: #191919;
                  margin: 16px auto 18px;
                }

                .write_box {
                  width: 940px;
                  height: 150px;
                  margin: 15px auto 260px;
                  position: relative;

                  h4 {
                    padding: 15px 0 20px;
                    margin: 25px 0 0 20px;
                    color: $color;
                  }

                  div {
                    width: 900px;
                    margin: 0 auto 8px;
                    font-size: 14px;
                    font-weight: 500;

                    input {
                      margin: 5px 0 2px;
                    }

                    &.userName {
                      display: flex;
                      flex-direction: column;
                      align-items: flex-start;

                      input {
                        width: 200px;
                        height: 20px;
                        border: none;
                        border-bottom: 1px solid #222;
                        line-height: 20px;
                      }
                    }

                    &.queTit {
                      display: flex;
                      flex-direction: column;
                      align-items: flex-start;

                      input {
                        width: 898px;
                        height: 20px;
                        border: none;
                        border-bottom: 1px solid #222;
                        line-height: 20px;
                      }
                    }

                    &.queCon {
                      margin-top: 15px;

                      input {
                        width: 900px;
                        height: 120px;
                        padding: 20px;
                        border: none;
                        box-sizing: border-box;
                        margin: 5px 0 10px;
                        background-color: #f5f5f5;
                      }
                    }
                  }

                  .queBtn {
                    position: absolute;
                    bottom: -215px;
                    right: 20px;
                    width: 58px;
                    height: 30px;
                    line-height: 31px;
                    border-radius: 5px;
                    border: none;
                    font-weight: 700;
                    background-color: $color;
                    color: #EFE8DA;
                    cursor: pointer;
                  }
                }

                .table_title {
                  width: 930px;
                  height: 30px;
                  display: flex;
                  justify-content: space-between;
                  align-items: center;
                  font-size: 14px;
                  font-weight: 500;
                  //margin-left: 113px;

                  div {
                    width: 95px;
                    text-align: center;

                    &.title_title {
                      width: 380px;
                      text-align: left;
                      margin-left: 25px;
                    }
                  }
                }

                .line_thin {
                  width: 930px;
                  height: 1px;
                  background-color: #191919;
                  margin: 12px auto;
                }

                .list_box {
                  width: 100%;
                  height: auto;
                  background-color: #fff;
                  margin-bottom: 150px;

                  ul {
                    width: 930px;
                    height: auto;
                    margin: 0 auto;

                    li {
                      width: 100%;
                      height: auto;
                      line-height: 20px;
                      font-size: 14px;
                      font-weight: 500;
                      padding: 5px 0;
                      cursor: pointer;
                      border-bottom: 1px solid #eee;

                      &:last-child {
                        border-bottom: none;
                      }

                      .short {
                        width: 930px;
                        height: 60px;
                        display: flex;
                        justify-content: space-between;
                        align-items: center;

                        &:hover {
                          background-color: rgb(249, 244, 238);
                        }

                        div {
                          width: 95px;
                          height: 60px;
                          text-align: center;
                          line-height: 55px;

                          &.que {
                            width: 385px;
                            text-align: left;
                            margin-left: 20px;
                          }

                          &.queDate {
                            width: 110px;
                            //margin-right: 20px;
                            font-size: 13px;
                          }

                          &.queStatus {
                            width: 50px;
                            height: 25px;
                            line-height: 23px;
                            border-radius: 20px;
                            background-color: #252117;
                            color: #EFE8DA;
                            text-align: center;
                            margin: 0 25px 0 0;
                          }

                          &.on {
                            background-color: $color;
                          }
                        }
                      }

                      .answer_area {
                        width: 100%;
                        height: auto;
                        background-color: #fff;
                        border-radius: 2px;
                        position: relative;
                        margin: 0 auto;
                        display: none;

                        .answer {
                          background-color: #EFE8DA;
                          padding: 30px 0 50px;
                          display: flex;

                          span {
                            width: 20px;
                            height: 20px;
                            text-align: center;
                            line-height: 20px;
                            padding: 20px;
                            font-size: 18px;
                            position: absolute;
                            top: 0;
                            right: 0;
                          }

                          div {
                            width: 50px;
                            height: 25px;
                            background-color: #fff;
                            border-radius: 20px;
                            text-align: center;
                            line-height: 25px;
                            margin: 0 15px 15px 20px;
                          }

                          p {
                            margin-top: 5px;
                            line-height: 12px;
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }


    .content_side {
      position: absolute;
      top: 0;
      right: 0;
      width: 760px;
      height: 100%;
      background-color: #fff;
      overflow-y: auto;
      overflow-x: hidden;

      &::-webkit-scrollbar {
        width: 10px;
        height: auto;
        background-color: #eee;
      }

      &::-webkit-scrollbar-thumb {
        width: 10px;
        background-color: #FD8234;
      }

      .side_info {
        position: relative;
        width: 760px;
        height: auto;
        margin: 0;
        background-color: #fff;
        box-sizing: border-box;

        .side_info_wrap {
          width: 760px;
          height: calc(100vh - 105px);

          .product_info {
            width: 100%;
            height: 30%;

            h4 {
              padding: 38px 60px 0;
              box-sizing: border-box;
              font-weight: 500;
            }

            h3 {
              padding: 5px 60px 20px;
              font-size: 21px;
            }

            span {
              font-size: 21px;
              font-weight: 500;
              padding: 0 60px;
            }

            .select_option {
              position: absolute;
              top: 185px;
              left: 155px;

              h5 {
                color: #222;
              }
            }

            .color {
              width: auto;
              height: 30px;
              display: flex;
              align-items: center;
              padding: 10px 60px;
              margin: 22px 0 0px;

              div {
                width: 18px;
                height: 18px;
                border-radius: 50%;
                margin-right: 8px;
                border: 2px solid #eee;
                cursor: pointer;

                &:hover {
                  border: 2px solid #222;
                }

                &.on {
                  border: 2px solid #222;
                }

                &.butter {
                  background-color: #ffd18b;
                }

                &.pink {
                  background-color: #e4cfce;
                }

                &.white {
                  background-color: #fff;
                }
              }
            }
          }

          .option {
            position: relative;
            width: 100%;
            height: 30%;
            border-top: 2px solid #eee;
            box-sizing: border-box;

            h5 {
              color: #414040;
              padding: 25px 60px 8px;
              box-sizing: border-box;
            }

            .option_switch_box {
              width: 155px;
              height: 155px;
              margin: 5px 0 0 60px;

              .option_switch {
                display: flex;
                justify-content: flex-start;
                align-items: center;
                border: 2px solid #EEE;
                box-sizing: border-box;
                cursor: pointer;

                div {
                  width: 155px;
                  height: 155px;
                  display: none;

                  &.on {
                    display: block;
                  }

                  .option_img {
                    width: 155px;
                    height: 155px;

                    img {
                      display: block;
                      width: 100%;
                      height: 100%;
                      object-fit: cover;
                    }
                  }
                }
              }
            }
          }

          .option_txt {
            position: relative;

            .optionText {
              position: absolute;
              top: -90px;
              left: 250px;
              text-align: left;
              font-size: 12px;
              display: none;

              &.on {
                display: block;
              }

              p {
                font-size: 14px;
                font-weight: bold;
              }

              p.price {
                font-size: 15px;
                padding: 4px 0;
              }
            }
          }

          .option_color {
            width: auto;
            height: 30px;
            display: flex;
            align-items: center;
            margin: -30px 0 0 250px;

            div {
              width: 15px;
              height: 15px;
              border-radius: 50%;
              margin-right: 5px;
              border: 2px solid #eee;
              cursor: pointer;

              &:hover {
                border: 2px solid #222;
              }

              &.on {
                border: 2px solid #222;
              }

              &.butter {
                background-color: #ffd18b;
              }

              &.green {
                background-color: darkgreen;
              }

              &.orange {
                background-color: orangered;
              }

              &.black {
                background-color: #000;
              }
            }
          }

          .buying_info {
            width: 100%;
            height: 19%;
            padding: 33px 0 0 65px;
            box-sizing: border-box;
            border-top: 2px solid #eee;
            font-size: 15px;

            .shipment {
              width: 100%;
              height: auto;
              display: flex;

              p {
                font-weight: bold;
                margin-right: 50px;
              }

              ul li {
                &:nth-child(2) {
                  padding: 2px 0;
                }
              }
            }

            .card_benefit {
              width: 100%;
              height: 46px;
              display: flex;
              align-items: center;
              margin-top: 10px;

              p {
                line-height: 46px;
                font-weight: bold;
                margin-right: 35px;
              }

              .more_mark {
                width: 30px;
                height: 30px;
                font-size: 17px;
                cursor: pointer;

                i {
                  color: $color;
                }
              }

              .card_popup {
                width: 255px;
                height: 310px;
                background-color: #f8f8f8;
                color: #191919;
                border-radius: 15px 15px 15px 0;
                position: absolute;
                top: 26%;
                left: 21.5%;
                padding: 20px 5px;
                box-sizing: border-box;
                box-shadow: rgba(0, 0, 0, 0.07) 0px 1px 1px, rgba(0, 0, 0, 0.07) 0px 2px 2px, rgba(0, 0, 0, 0.07) 0px 4px 4px, rgba(0, 0, 0, 0.07) 0px 8px 8px, rgba(0, 0, 0, 0.07) 0px 16px 16px;
                display: none;


                h3 {
                  display: inline-block;
                  font-size: 16px;
                  padding: 20px 34px;
                }

                h4 {
                  display: inline-block;
                  padding: 0px 34px 10px;
                }

                .line_thin {
                  width: 75%;
                  height: 1px;
                  background-color: #191919;
                  margin: 5px auto 12px;
                }

                ul {

                  li {
                    width: 97%;
                    height: 35px;
                    display: flex;
                    justify-content: space-evenly;
                    margin-left: 20px;

                    p {
                      font-size: 13px;

                      &:first-child {
                        color: $color;
                      }
                    }
                  }
                }
              }
            }
          }

          .allPrice {
            width: 100%;
            height: 11%;
            display: flex;
            justify-content: flex-start;
            //justify-content: space-between;
            align-items: flex-end;
            padding-left: 60px;
            border-top: 2px solid #eee;
            box-sizing: border-box;
            line-height: 88px;

            p {
              font-size: 16px;
              font-weight: 500;
            }

            .priceCount {
              display: flex;
              justify-content: flex-start;
              align-items: baseline;
              margin: 0 45px 0 60px;

              input {
                width: 35px;
                height: 35px;
                border: none;
                font-size: 16px;
                font-weight: bold;
                text-align: center;
                cursor: pointer;

                &:nth-child(2) {
                  margin-left: 140px;
                }

                &:nth-child(3) {
                  border: 2px solid #eee;
                  box-sizing: border-box;
                }

                &:nth-child(5) {
                  border: none;
                  width: 100px;
                  height: 50px;
                  font-size: 18px;
                  font-weight: bold;
                }

                &.hover {
                  background-color: #eee;
                }

                &.hover:hover {
                  color: $color;
                }
              }

              p.won {
                font-size: 18px;
                font-weight: bold;
                margin-left: -10px;
              }
            }

            .finalAmount {
              display: flex;
              align-items: baseline;

              input {
                width: 30px;
                height: 30px;
                border-radius: 50%;
                background-color: $color;
                margin: 10px 5px 10px 15px;
                line-height: 30px;
                font-size: 18px;
                font-weight: bold;
                border: none;
              }

              p {
                font-size: 18px;
                font-weight: bold;
                margin-left: 2px;
                line-height: 28px;
              }
            }
          }

          .shopping_button {
            width: 100%;
            height: 10%;
            display: flex;

            div {
              width: 50%;
              height: 85px;
              font-weight: 22px;
              font-weight: 500;
              letter-spacing: 0.5px;
              text-align: center;
              line-height: 85px;
              border: 1px solid #EFE8DA;
              box-sizing: border-box;
              box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
              /* box-shadow: rgba(50, 50, 93, 0.25) 0px 6px 12px -2px, rgba(0, 0, 0, 0.3) 0px 3px 7px -3px; */
              cursor: pointer;

              &.buying_btn {
                background-color: #EFE8DA;
                color: #191919;
              }

              &.buying_btn:hover {
                background-color: #252117;
                border: 1px solid #252117;
                color: $color;
              }

              &.cart_btn {
                color: #191919;
              }

              &.cart_btn:hover {
                background-color: #252117;
                border: 1px solid #252117;
                color: $color;
              }
            }
          }
        }
      }



      .side_notice {
        width: 100%;
        height: auto;
        padding-bottom: 65px;

        div {
          width: 100%;
          height: auto;
          position: relative;

          .title {
            width: 100%;
            height: 80px;
            background-color: #fff;
            border-bottom: 1px solid #eee;
            box-shadow: rgba(0, 0, 0, 0.18) 0px 2px 4px;
            cursor: pointer;

            h4 {
              line-height: 80px;
              padding-left: 60px;
              font-weight: 500;
            }
          }

          .openClose {
            position: absolute;
            top: 30px;
            left: 680px;
            cursor: pointer;

            div {
              display: none;

              &.minus_btn {
                position: absolute;
                top: 0px;
              }

              &.show {
                display: block;
              }

              i {
                font-size: 14px;
                color: #191919;
              }
            }
          }

          .hide {
            width: 100%;
            height: auto;
            padding-bottom: 20px;
            box-sizing: border-box;
            background-color: #f1f1f1;
            box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 1px 3px 1px;
            display: none;

            &.show {
              display: block;
            }

            p {
              padding: 30px 60px;
            }
          }
        }
      }
    }
  }
}


/* ----------------------- top_btn ----------------------- */
.top_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #191919;
  position: fixed;
  top: 185px;
  right: 25px;
  text-align: center;
  z-index: 999;
  cursor: pointer;
  opacity: 0;

  i {
    text-align: center;
    line-height: 40px;
    color: #fff;
  }
}

.history_btn {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #191919;
  position: fixed;
  top: 135px;
  right: 25px;
  text-align: center;
  z-index: 999;
  cursor: pointer;


  i {
    text-align: center;
    line-height: 40px;
    color: #fff;
  }
}


/* ----------------------- shopping_history ----------------------- */
.history {
  .history_popup {
    position: fixed;
    right: 0;
    bottom: 0;
    width: 480px;
    height: 100vh;
    background-color: #fff;
    transition: 0.4s all ease;
    z-index: 9999999999 !important;
    box-shadow: rgba(149, 157, 165, 0.2) 0px 8px 24px;
    display: none;

    h4 {
      padding: 50px 30px 40px;
    }

    .history_hide {
      width: 25px;
      height: 25px;
      text-align: center;
      position: absolute;
      top: 45px;
      right: 25px;
      cursor: pointer;
    }

    p.today {
      font-size: 15px;
      padding: 0 30px;
      margin-bottom: 40px;
    }

    .shopping_history {
      display: block;

      .history_list {
        position: relative;
        width: 420px;
        height: 120px;
        background-color: #fcfcfc;
        border: none;
        margin: 0 auto 50px;
        display: block;
        cursor: pointer;

        .list_img {
          width: 100px;
          height: 120px;

          img {
            width: 100%;
            height: 100%;
          }
        }

        .list_txt {
          width: 250px;
          height: auto;
          position: absolute;
          top: 32px;
          right: 50px;

          .list_delete {
            width: 25px;
            height: 25px;
            text-align: center;
            position: absolute;
            top: -24px;
            left: 268px;
            display: none;
            color: #ccc;
          }

          p {
            width: 250px;
            text-align: left;
            font-size: 13px;
            display: block;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
          }

          span {
            position: absolute;
            top: 30px;
            font-size: 13px;
            font-weight: bold;
          }
        }
      }
    }

    .deleteAll {
      width: 120px;
      height: 45px;
      position: absolute;
      bottom: 60px;
      right: 30px;
      border-radius: 2px;
      background-color: #eee;
      cursor: pointer;

      &:hover {
        background-color: #252117;
      }

      &:hover p {
        color: #f8f8f8;
      }

      p {
        line-height: 45px;
        text-align: center;
        font-size: 13px;
      }
    }
  }
}
