  width: 100%;
  height: auto;

  .swiper_main {
    width: auto;
    height: calc(100vh - 15px);


    &:hover .swiperBtn {
      visibility: visible;
      transition: all 0.3s;
    }

    .swiper-wrapper {
      width: auto;
