  /* ----------------------- place ----------------------- */
  .issue_contents {
    width: 100vw;
    height: auto;

    .space_wrap {
      width: 1180px;
      height: auto;
      margin: 0 auto;

      h1 {
        text-align: center;
        padding: 10px 0;
        margin: 75px auto 50px;
      }

      .space_label {
        width: 800px;
        height: 50px;
        margin: 10px auto 20px;
        display: flex;
        justify-content: space-evenly;
        align-items: flex-start;

        li {
          width: 80px;
          font-weight: bold;
          text-align: center;
          cursor: pointer;

          &.on {
            padding-bottom: 8px;
            border-bottom: 5px solid $color;
            transition: all 0.1s;
          }
        }
      }

      .space_box {
        width: 1180px;
        height: 460px;
        display: flex;

        .img_left {
          width: 460px;
          height: 460px;
          margin-right: 20px;
          position: relative;

          .interior {
            width: 460px;
            height: 460px;
            position: absolute;
            top: 0;
            left: 0;
            display: none;

            &.left_on {
              display: block;
            }
          }

          .interior li {
            width: 460px;
            height: 460px;
            position: absolute;
            top: 0;
            left: 0;
            display: none;

            &.left_on {
              display: block;
            }

            img {
              width: 100%;
              height: 100%;
            }
          }
        }

        .img_right {
          width: 720px;
          height: 460px;

          .product {
            width: 720px;
            height: 460px;

            //overflow: hidden;
            display: none;

            &.right_on {
              display: block;
            }
          }

          li {
            width: 220px;
            height: 220px;
            float: left;
            margin: 0 20px 20px 0;


            &:nth-child(3) {
              margin-right: 0;
            }

            img {
              width: 100%;
              height: 100%;
            }
          }
        }
      }
    }
  }
